  {
    return $this->getDateTimeObject('fecha')->format('d/m/Y').': BoletÃ­n '.$this->getProyectoLey()->getNroBoletin()." (".$this->getCamara().")";
  }
  
  public function getVotacionComisionOrdered()
  {
    $q = Doctrine_Query::create()
      ->select('v.*')
      ->from('VotacionComision v')
      ->where('v.id_debate = ?', $this->getIdDebate())
      ->orderBy('v.tipo ASC');

    return $q->execute();
