class VotacionComision extends BaseVotacionComision
{
  public function getVoto($id_parlamentario)
  {
    $voto = Doctrine_Core::getTable('VotacionComisionParlamentario')->find(array($this->getIdVotacion(),$id_parlamentario));
    if ($voto != null) return $voto->getVoto();
    else return null;
  }

  public function getVotosVoto($voto)
  {
    return Doctrine_Core::getTable('VotacionComisionParlamentario')->createQuery('v')->where('v.id_votacion = ?', $this->getIdVotacion())->andWhere('v.voto = ?', $voto)->execute();
  }

  public function getVotosAus()
  {
    ($this->getCamara() == 'C.Diputados') ? $camara='D' : $camara='S';
    $parlamentarios = Doctrine_Core::getTable('Parlamentario')->createQuery('p')->where('p.senador_diputado = ?', $camara)->andWhere('p.activo = ?', 1)->execute();
    $ausentes = new Doctrine_Collection('Parlamentario');
    foreach ($parlamentarios as $i=>$parl){
      if (Doctrine_Core::getTable('VotacionComisionParlamentario')->find(array($this->getIdVotacion(),$parl->getIdParlamentario())) == null) $ausentes->add($parl);
    }
    return $ausentes;
  }

  public function getVotos()
  {
    return Doctrine_Core::getTable('VotacionComisionParlamentario')->createQuery('v')->where('v.id_votacion = ?', $this->getIdVotacion())->execute();
  }
}
